import React, { Component } from 'react';
import axios from 'axios';
import ItemLists from "./components/itemlist/list";
import './Css/Main.css';

class Main extends Component {
    constructor(props) {
        super(props);
        this.state = {
            category: 'life',
            ItemList: []
        }
    }
    componentDidMount() {
        this._getList();
    }
    _getList() {
        const apiUrl = 'dummy/ItemList.json';
        axios.get(apiUrl)
            .then(data => { //가지고 온 리스트를 저장
                this.setState({
                    ItemList: data.data.ItemList,
                    category: 'life'
                });
            })
            .catch(error => {
                console.log(error);
            });
    }

    render() {
        var items = [];
        var demandItems = [];
        var supplyItems = [];
        items = this.state.ItemList.filter(item => (
            item.category === this.state.category
        ))
        demandItems = items.filter(item => (
            item.isDemand
        ))
        supplyItems = items.filter(item => (
            !item.isDemand
        ))
        return (
            <div class = 'main_wrap'>
                <div class = 'main'>
                    <div>빌려주세요 ㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠㅠ</div>
                    <div id = 'demands'>
                        <ItemLists list = {demandItems}/>
                    </div>
                    <div>빌려가세요~~~~~~~~~~~~~~~~~~~~~~~~~~</div>
                    <div id = 'supplies'>
                        <ItemLists list = {supplyItems}/>
                    </div>
                </div>
            </div>
        );
    }
}

export default Main;
