        super(props);
        this.state = {
            ItemList: []
        }
    }

    componentDidMount() { //있어야 하나? -th
        this._getList();
    }

    _getList() {
        const apiUrl = '/data/dummy/ItemList.json'; // 나중에 src/data/dummy/ItemList.json 절대경로로 수정하기
        axios.get(apiUrl)
            .then(data => { //가지고 온 리스트를 저장
                console.log(apiUrl, "saved");
                this.setState({
                    ItemList: data.data.ItemList
                });
            })
            .catch(error => {
                console.log(error);
            });
    }

